import React from 'react'

const PasswordScore = ({ password }) => {

  const getColor = () => {


      if (!/(?=.*[0-9])>1/.test(password)){
        alert("password mora da sadrzi minimum 1 broj");}
        if(!/password.length < 12/.test(password)){
        alert("password mora biti duzine minimum 12 karaktera");}
        if (!/lowercase < 2/.test(password)) {
        alert("Password mora da sadrzi 2 mala slova");}
        if (!/uppercase < 2/.test(password)) {
        alert("Password mora da sadrzi 2 velika slova");}
        if (!/(?=.*['.' , ',' , '?','!',';',':','""'])>1/.test(password)){
        alert("password mora da sadrzi minimum jedan znak interpunkcije");}


    }


  return (
    <div className="pass-score" title={`Password is ${password}% valid`}>
      <div
        className="password"
        style={{
          width: `${password}%`,
          backgroundColor: getColor()
        }}
      />
    </div>
  )
}

export default PasswordScore
