// eslint-disable-next-line
import React from 'react'

function Form() {
  const [getPassword, setPassword] = useState("")
  const [getBarStatus, setBarStatus] = useState(false)
  let len = getPassword.length / 30 * 100;
  let color = () => {
    if(getPassword.length < 8) {
      return "bg-danger"
    } else if(getPassword.length <= 12) {
      return "bg-warning"
    } else if(getPassword.length <= 18) {
      return "bg-info"
    } else {
      return "bg-success"
    }
  }
  const renderBar = () => {
    if(getBarStatus === true) {
      return(
        <div>
          <div className="progress">
        <div 
          className={`progress-bar ${color()}`}
          role="progressbar" 
          style={{width: `${len}%`}}
          aria-valuenow={`${len}`} 
          aria-valuemin="0" 
          aria-valuemax="100"
          />
      </div>
          <br/>
        <p>The progress is based on password length, however you can easily implement your own logic. <br /> This code isn't refactored</p>
        </div>

    )
    }
    return null;
  }
  return (
    <form className="container-sm form">
      <h2>Start entering some text</h2>
      <div className="form-group">
        <label for="password">Password</label>
        <input type="password" id="password" className="form-control"
          value={getPassword}
          minLength={8}
          maxLength={30}
          onChange={(event) => setPassword(event.target.value)}
          onClick={() => setBarStatus(true)}
        />
      </div>
      {getBarStatus === true? renderBar() : null}
    </form>
  )
}
